//Objective:Find the minimum of two numbers using ternary operators

public class Q10 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 5, y = 3;
		
		int z = (x < y) ? x : y ;

		System.out.println("The min value is " + z);
	}
}
